import { longestPalindrome } from "./longest-palindrome";

describe("longestPalindrome", () => {
  test("test-1", () => {
    expect(longestPalindrome("abccccdd")).toBe(7);
  });
  test("test-2", () => {
    expect(longestPalindrome("a")).toBe(1);
  });
  test("test-3", () => {
    const input =
      "";
    expect(longestPalindrome(input)).toBe(983);
  });
});
